        pdf: '/doc/SchemaTech_2.pdf',
        buttons: ['Activiteiten schema', 'Class diagram', 'Dataschema', 'Mock-up'],
        images: ['/images/Schema2_ActiviteitenDiagram.png', '/images/Schema2_ClassDiagram.png', '/images/Schema2_Dataschema.png', '/images/Schema2_Mockup.png']
      }, {
