      }]
    }];

    // Reactive state: selectie van afbeeldingen per onderdeel en document
    const selectedImages = reactive({});

    // Default selectie: alle documenten starten met knop 1 geselecteerd
    onderdelen.forEach((_, onderdeelIndex) => {
      selectedImages[onderdeelIndex] = {};
      for (let doc = 1; doc <= 3; doc++) {
        selectedImages[onderdeelIndex][doc] = 1;
      }
    });

    // Functie om afbeelding te selecteren
    function selectImage(onderdeelIndex, docIndex, btnNumber) {
      selectedImages[onderdeelIndex][docIndex] = btnNumber;
    }

    // Functie om juiste afbeelding te retourneren
    function getImageSrc(onderdeelIndex, docIndex, btnNumber) {
      if (onderdelen[onderdeelIndex] && onderdelen[onderdeelIndex].docs[docIndex - 1] && onderdelen[onderdeelIndex].docs[docIndex - 1].images[btnNumber - 1]) {
        return onderdelen[onderdeelIndex].docs[docIndex - 1].images[btnNumber - 1];
      }
      return ''; // fallback indien niet gevonden
    }
    const __returned__ = {
      onderdelen,
      selectedImages,
      selectImage,
      getImageSrc,
      reactive
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
